mapper.select:
WITH people AS( SELECT doc_no , mem_no , null as approval_status , null as approval_order FROM referer UNION SELECT doc_no , mem_no , approval_status , approval_order FROM approval_line ) SELECT ad.doc_no "doc_no" , ad.mem_no "mem_no" , mem_name , p.mem_no "approvalmem_no" , doc_title , doc_enroll_date , doc_status , doc_form , approval_status , approval_order FROM people p INNER JOIN approval_document ad ON p.doc_no = ad.doc_no JOIN employee e on e.mem_no = ad.mem_no WHERE p.mem_no = #{memNo} and doc_title like '%' || #{docTitle} || '%'
and doc_form = #{docForm}
ORDER BY ad.doc_no desc

mapper.update:
update 
       approval_line
   set approval_status = #{approvalStatus}
 where doc_no = #{docNo}

   and approval_grade >= #{approvalOrder}


   and approval_grade <= #{approvalOrder}

mapper.insert:
insert
  into approval_document
  (
     doc_no
   , mem_no
   , doc_title
   , doc_contents
   , doc_enroll_date
   , doc_retention
   , doc_grade
   , doc_status
   , doc_form
   , doc_approval_status
  )
  values
  (
     seq_apno.nextval
   , #{memNo}
   , #{docTitle}
   , #{docContents}
   , #{docEnrollDate}
   , #{docRetention}
   , #{docGrade}
   , #{docStatus}
   , #{docForm}
   , #{docApprovalStatus}
  )

insert
  into approval_attachment
  (
     att_no
   , doc_no
   , att_origin_name
   , att_change_name
   , att_path
   , att_loadpath
  )
  values
  (
     seq_aano.nextval
   , seq_apno.currval
   , #{attOriginName}
   , #{attChangeName}
   , #{attPath}
   , #{attLoadpath}
  )
