mapper.insert:
insert
  into approval_document
  (
     doc_no
   , mem_no
   , doc_title
   , doc_contents
   , doc_enroll_date
   , doc_retention
   , doc_grade
   , doc_status
   , doc_form
   , doc_approval_status
  )
  values
  (
     seq_apno.nextval
   , #{memNo}
   , #{docTitle}
   , #{docContents}
   , #{docEnrollDate}
   , #{docRetention}
   , #{docGrade}
   , #{docStatus}
   , #{docForm}
   , #{docApprovalStatus}
  )

insert
  into approval_attachment
  (
     att_no
   , doc_no
   , att_origin_name
   , att_change_name
   , att_path
   , att_loadpath
  )
  values
  (
     seq_aano.nextval
   , seq_apno.currval
   , #{attOriginName}
   , #{attChangeName}
   , #{attPath}
   , #{attLoadpath}
  )
