mapper.select:
SELECT MEM_ID
	  , MEM_NAME
  FROM EMPLOYEE
  WHERE MEM_NAME = #{memName}
    AND MEM_BIRTH = #{birth}
    AND MEM_PHONE = #{phone}
    AND MEM_STATUS = 'Y'

SELECT MEM_PWD
	  , MEM_NAME
	  , MEM_ID
  FROM EMPLOYEE
  WHERE MEM_NAME = #{memName}
    AND MEM_ID = #{memId}
    AND MEM_BIRTH = #{birth}
    AND MEM_PHONE = #{phone}
    AND MEM_STATUS = 'Y'

mapper.update:
UPDATE
       EMPLOYEE
   SET MEM_PWD = #{memPwd}
     , MODIFY_DATE = SYSDATE
 WHERE MEM_ID = #{memId}

mapper.insert:
INSERT
  INTO BUSINESS_TRIP
  (
       BUSINESS_NO
     , BU_PROPOSER 
     , CREATE_DATE
     , BUSINESS_START
     , BUSINESS_END
     , BUSINESS_PLACE
     , BUSINESS_PHONE
     , BUSINESS_PURPOSE
     , BUSINESS_PLAN
     , BUDGET
     , BU_STATUS
  )
  VALUES
  (
       SEQ_BNO.NEXTVAL
     , #{buProposer}
     , DEFAULT
     , #{businessStart}
     , #{businessEnd}
     , #{businessPlace}
     , #{businessPhone}
     , #{businessPurpose}
     , #{businessPlan}
     , #{budget}
     , DEFAULT
  )
