mapper.select:
SELECT MEM_ID
	  , MEM_NAME
  FROM EMPLOYEE
  WHERE MEM_NAME = #{memName}
    AND MEM_BIRTH = #{birth}
    AND MEM_PHONE = #{phone}
    AND MEM_STATUS = 'Y'

SELECT MEM_PWD
	  , MEM_NAME
	  , MEM_ID
  FROM EMPLOYEE
  WHERE MEM_NAME = #{memName}
    AND MEM_ID = #{memId}
    AND MEM_BIRTH = #{birth}
    AND MEM_PHONE = #{phone}
    AND MEM_STATUS = 'Y'

mapper.update:
UPDATE
       EMPLOYEE
   SET MEM_PWD = #{memPwd}
     , MODIFY_DATE = SYSDATE
 WHERE MEM_ID = #{memId}

mapper.insert:
INSERT
  INTO BUSINESS_TRIP
  (
       BUSINESS_NO
     , BU_PROPOSER 
     , CREATE_DATE
     , BUSINESS_START
     , BUSINESS_END
     , BUSINESS_PLACE
     , BUSINESS_PHONE
     , BUSINESS_PURPOSE
     , BUSINESS_PLAN
     , BUDGET
     , BU_STATUS
  )
  VALUES
  (
       SEQ_BNO.NEXTVAL
     , #{buProposer}
     , DEFAULT
     , #{businessStart}
     , #{businessEnd}
     , #{businessPlace}
     , #{businessPhone}
     , #{businessPurpose}
     , #{businessPlan}
     , #{budget}
     , DEFAULT
  )

mapper.update:
UPDATE
       BUSINESS_TRIP
   SET BU_STATUS = 'R'
     , BU_APPROVER = #{apno}
 WHERE BUSINESS_NO = #{btno}

UPDATE
       BUSINESS_TRIP
   SET BU_STATUS = 'Y'
     , BU_APPROVER = #{apno}
 WHERE BUSINESS_NO = #{btno}

mapper.insert:
INSERT
  INTO ANNUAL_LEAVE
  (
       ANNUAL_NO
     , AN_PROPOSER 
     , CREATE_DATE
     , ANNUAL_START
     , ANNUAL_END
     , EMERGENCY_PHONE
     , REASON
     , AN_STATUS
  )
  VALUES
  (
       SEQ_ANO.NEXTVAL
     , #{anProposer}
     , DEFAULT
     , #{annualStart}
     , #{annualEnd}
     , #{emergencyPhone}
     , #{reason}
     , DEFAULT
  )

mapper.update:
UPDATE
       ANNUAL_LEAVE
   SET AN_STATUS = 'R'
     , AN_APPROVER = #{apno}
 WHERE ANNUAL_NO = #{anno}

UPDATE
       ANNUAL_LEAVE
   SET AN_STATUS = 'Y'
     , AN_APPROVER = #{apno}
 WHERE ANNUAL_NO = #{anno}
