mapper.insert:
insert 
  into calendar
     (
       cal_no
     , cal_title
     , cal_place
     , cal_content
     , cal_start_date
     , cal_end_date
     , cal_time
     , cal_color
     , cal_range
     , mem_no
     )
values
     (
       seq_calno.nextval
     , #{calTitle}
     , #{calPlace}
     , #{calContent}
     , #{calStartDate}
     , #{calEndDate}
     , #{calTime}
     , #{calColor}
     , #{calRange}
     , #{memNo}
     )
