mapper.select:
select er.em_to
  from email_recipient er
 where er.em_no = #{emNo}

select count(*)
  from email_attach ea
 where ea.em_no = #{emNo}

mapper.update:
update email_recipient
   set em_read = 'Y'
 where em_to = #{emTo}
   and em_rec_no = #{emRecNo}
   and em_status = 'Y'

mapper.insert:


	select seq_emno.nextval from dual

insert 
  into email
  (
    em_no
  , em_from
  , em_title
  , em_content
  )
values 
  (
    #{emNo}
  , #{emFrom}
  , #{emTitle}
  , #{emContent}
  )

insert 
  into email_attach
  (
    em_att_no
  , em_origin_name
  , em_change_name
  , em_no
  )
 values
  (
    seq_emfno.nextval
  , #{emOriginName}
  , #{emChangeName}
  , #{emNo}
  )

insert 
  into email_recipient
  ( 
    em_rec_no
  , em_to
  , em_no
  ) 
values
  (
    seq_emrno.nextval
  , #{emTo}
  , #{emNo}
  )

mapper.update:
update email
   set em_inp = 'Y'
 where em_no = #{emNo}
   and em_from = #{emFrom}

update email
   set em_inp = 'N'
 where em_no = #{emNo}
   and em_from = #{emFrom}

mapper.insert:


	select seq_emno.nextval from dual

insert 
  into email
  (
    em_no
  , em_from
  , em_title
  , em_content
  )
values 
  (
    #{emNo}
  , #{emFrom}
  , #{emTitle}
  , #{emContent}
  )

mapper.update:
update email
   set em_status = 'N'
 where em_from = #{emFrom}
   and em_no = #{emNo}

update email_recipient
   set em_inp = 'Y'
 where em_rec_no = #{emRecNo}
   and em_to = #{emTo}

update email_recipient
   set em_inp = 'N'
 where em_rec_no = #{emRecNo}
   and em_to = #{emTo}

update email_recipient
   set em_status = 'N'
 where em_rec_no = #{emRecNo}
   and em_to = #{emTo}
