mapper.insert:
insert
	  into EMPLOYEE
	  (  MEM_NO
	   , MEM_ID
	   , MEM_PWD
	   , MEM_IMG
	   , MEM_NAME
	   , MEM_PHONE
	   , MEM_EMAIL
	   , MEM_GENDER
	   , MEM_BIRTH
	   , MEM_ADDRESS
	   , MEM_CERTIFICATION
	   , MEM_CAREER
	   , MEM_STATUS
	   , MEM_ENROLL
	   , DEPT_CODE
	   , JOB_CODE
	  )
	  VALUES
	  (  SEQ_ENO.NEXTVAL
	   , #{memId}
	   , #{memPwd}
	   , #{memImg}
	   , #{memName}
	   , #{phone}
	   , #{email}
	   , #{gender}
	   , #{birth}
	   , #{address}
	   , #{certification}
	   , #{career}
	   , 'Y'
	   , #{enrollDate}
	   , #{deptCode}
	   , #{jobCode}
	  )

mapper.update:
UPDATE
        EMPLOYEE
   SET  MEM_ID = #{memId}
   	  , MEM_PWD = #{memPwd}
      , MEM_IMG = #{memImg}
      , MEM_NAME = #{memName}
      , MEM_PHONE = #{phone}
      , MEM_EMAIL = #{email}
      , MEM_GENDER = #{gender}
      , MEM_BIRTH = #{birth}
      , MEM_ADDRESS = #{address}
      , MEM_CERTIFICATION = #{certification}
      , MEM_CAREER = #{career}

      , MEM_ENROLL = #{enrollDate}
      , DEPT_CODE = #{deptCode}
      , JOB_CODE = #{jobCode}
  WHERE MEM_NO = #{memNo}
