mapper.insert:
insert into board_attachment ( bo_att_no, bo_origin_name, bo_change_name, file_path, bo_division, ref_bno ) values ( seq_bano.nextval, #{attach.originName}, #{attach.changeName}, #{attach.filePath}, '1',
#{supportNo}
seq_wsno.currval
)

mapper.select:

select
	   count(support_no)
  from work_support

	  	   , department

 where status = 'Y'
   and r_dept_code like #{deptCode}



		   and ${condition} like #{keyword}


			and category = #{category}

select support_no, category, r_dept_code, s_dept_code, support_title, support_content, start_date, end_date, mem_no, d.dept_name, create_date from work_support w, department d where s_dept_code = d.dept_code and w.status = 'Y' and r_dept_code like #{deptCode}
and ${condition} like #{keyword}
and category = #{category}
order by 1 desc

mapper.update:
update 
	   board_attachment
   set bo_origin_name = #{attach.originName},
   	   bo_change_name = #{attach.changeName},
   	   file_path = #{attach.filePath}
 where ref_bno = #{supportNo}
